import React from 'react';
import { Form, Input, Button, Image, Message, Header, Icon } from 'semantic-ui-react';
import axios from 'axios';
import baseUrl from './utils/baseUrl';
import catchErrors from './utils/catchErrors';

const INITIAL_RECEIPT = {
  media: '',
};

function App() {
  const [receipt, setReceipt] = React.useState(INITIAL_RECEIPT);
  const [mediaPreview, setMediaPreview] = React.useState('');
  const [success, setSuccess] = React.useState(false);
  const [loading, setLoading] = React.useState(false);
  const [disabled, setDisabled] = React.useState(true);
  const [error, setError] = React.useState('');

  React.useEffect(() => {
    const isReceipt = Object.values(receipt).every((el) => Boolean(el));
    isReceipt ? setDisabled(false) : setDisabled(true);
  }, [receipt]);

  function handleChange(event) {
    const { name, value, files } = event.target;
    if (name === 'media') {
      setReceipt((prevState) => ({ ...prevState, media: files[0] }));
      setMediaPreview(window.URL.createObjectURL(files[0]));
    } else {
      setReceipt((prevState) => ({ ...prevState, [name]: value }));
    }
  }

  async function handleImageUpload() {
    // const data = new FormData();
    // data.append('picture', receipt.media);
    const response = await axios.get(
      'https://custom-ocr.klippa.com/api/v1?X-Auth-Key=Bm4CTHGza7FWYE8t6gtkGezwjcZrFrrn',
      // data,
    );
    console.log(response);
  }

  async function handleSubmit() {
    try {
      setLoading(true);
      setError('');
      const mediaUrl = await handleImageUpload();
      const url = `${baseUrl}/api/product`;
      const { name, price, description } = receipt;
      const payload = { name, price, description, mediaUrl };
      await axios.post(url, payload);
      setReceipt(INITIAL_RECEIPT);
      setSuccess(true);
    } catch (error) {
      catchErrors(error, setError);
    } finally {
      setLoading(false);
    }
  }

  return (
    <>
      <Header as="h2" block color="green">
        <Icon name="file" color="green" />
        Upload Receipt or Invoice
      </Header>
      <Form loading={loading} error={Boolean(error)} success={success} onSubmit={handleImageUpload}>
        <Message
          error
          header="Oops! Please check your upload presets on your Klippa account. "
          content={error}
        />
        <Message success icon="check" header="Success!" content="Your file(s) has been uploaded" />
        <Form.Group widths="equal">
          <Form.Field
            control={Input}
            name="media"
            type="file"
            label="Media"
            // accept="multipart/formdata"
            accept="image/*"
            content="Select Image"
            onChange={handleChange}
          />
        </Form.Group>
        <Image src={mediaPreview} rounded centered size="small" />
        {/* <Form.Field
          control={Button}
          disabled={loading}
          size="mini"
          color="green"
          icon="add alternate"
          content="Try Again"
          type="button"
        /> */}
        <Form.Field
          control={Button}
          disabled={disabled || loading}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          type="submit"
        />
      </Form>
    </>
  );
}

export default App;
