import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Image, Message, Header, Icon } from 'semantic-ui-react';
import axios from 'axios';
import catchErrors from './utils/catchErrors';

const INITIAL_RECEIPT = {
  media: '',
};

const App = () => {
  const [receipt, setReceipt] = useState(INITIAL_RECEIPT);
  const [mediaPreview, setMediaPreview] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const [disabled, setDisabled] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const isReceipt = Object.values(receipt).every((el) => Boolean(el));
    isReceipt ? setDisabled(false) : setDisabled(true);
  }, [receipt]);

  const handleChange = (event) => {
    const { files } = event.target;
    setReceipt((prevState) => ({ ...prevState, media: files[0] }));
    setMediaPreview(window.URL.createObjectURL(files[0]));
  };

  // function fileUpload() {
  //   const data = new FormData();
  //   data.append('image', receipt.media, receipt.media.name);
  //   axios
  //     .post(
  //       'https://custom-ocr.klippa.com/api/v1?X-Auth-Key=Bm4CTHGza7FWYE8t6gtkGezwjcZrFrrn',
  //       data,
  //     )
  //     .then((response) => console.log(response));
  // }

  const handleSubmit = async () => {
    try {
      setLoading(true);
      setError('');
      const data = new FormData();
      data.append('picture', receipt.media, receipt.media.name);
      const response = await axios.post(
        'https://custom-ocr.klippa.com/api/v1?X-Auth-Key=Bm4CTHGza7FWYE8t6gtkGezwjcZrFrrn',
        data,
      );
      console.log(response);
      setReceipt(INITIAL_RECEIPT);
      setSuccess(true);
    } catch (error) {
      catchErrors(error, setError);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Header as="h2" block color="green">
        <Icon name="file" color="green" />
        Upload Receipt or Invoice
      </Header>
      <Form loading={loading} error={Boolean(error)} success={success} onSubmit={handleSubmit}>
        <Message
          error
          header="Oops! Please check your upload presets on your Klippa account. "
          content={error}
        />
        <Message success icon="check" header="Success!" content="Your file(s) has been uploaded" />
        <Form.Field
          control={Input}
          name="media"
          type="file"
          label="Media"
          accept="image/*"
          content="Select Image"
          onChange={handleChange}
        />
        <Image src={mediaPreview} rounded centered size="small" />
        <Form.Field
          control={Button}
          disabled={loading}
          size="mini"
          color="green"
          icon="add alternate"
          content="Try Again"
          type="button"
        />
        <Form.Field
          control={Button}
          disabled={disabled || loading}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          type="submit"
        />
      </Form>
    </>
  );
};

export default App;
