import React, { useState, useEffect } from 'react';
import { Form, Input, Button, Image, Message, Header, Icon } from 'semantic-ui-react';
import axios from 'axios';
import catchErrors from './utils/catchErrors';
<div style={{ margin: '2em 0' }}>
  <Header as="h2">
    <Icon name="settings" />
    User Permissions
  </Header>
  <Table compact celled definition>
    <Table.Header>
      <Table.Row>
        <Table.HeaderCell />
        <Table.HeaderCell>Name</Table.HeaderCell>
        <Table.HeaderCell>Email</Table.HeaderCell>
        <Table.HeaderCell>Joined</Table.HeaderCell>
        <Table.HeaderCell>Updated</Table.HeaderCell>
        <Table.HeaderCell>Role</Table.HeaderCell>
      </Table.Row>
    </Table.Header>

    <Table.Body>
      {users.map((user) => (
        <UserPermission key={user._id} user={user} />
      ))}
    </Table.Body>
  </Table>
</div>;

const INITIAL_RECEIPT = {
  image: '',
};

const App = () => {
  const [receipt, setReceipt] = useState(INITIAL_RECEIPT);
  const [imagePreview, setImagePreview] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const [disabled, setDisabled] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const isReceipt = Object.values(receipt).every((el) => Boolean(el));
    isReceipt ? setDisabled(false) : setDisabled(true);
  }, [receipt]);

  const handleChange = (event) => {
    const { files } = event.target;
    setReceipt((prevState) => ({ ...prevState, image: files[0] }));
    setImagePreview(window.URL.createObjectURL(files[0]));
  };

  const handleSubmit = async () => {
    try {
      setLoading(true);
      setError('');
      const data = new FormData();
      data.append('picture', receipt.image, receipt.image.name);
      const response = await axios.get(
        'https://custom-ocr.klippa.com/api/v1?X-Auth-Key=Bm4CTHGza7FWYE8t6gtkGezwjcZrFrrn',
        data,
      );
      console.log(response.data.data);
      setReceipt(INITIAL_RECEIPT);
      setImagePreview('');
      setSuccess(true);
    } catch (error) {
      catchErrors(error, setError);
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Header as="h2" block color="green">
        <Icon name="file" color="green" />
        Upload Receipt or Invoice
      </Header>
      <Form loading={loading} error={Boolean(error)} success={success} onSubmit={handleSubmit}>
        <Message
          error
          header="Oops! Please check your upload presets on your Klippa account. "
          content={error}
        />
        <Message success icon="check" header="Success!" content="Your file(s) has been uploaded" />
        <Form.Field
          control={Input}
          name="image"
          type="file"
          label="Image"
          accept="image/*"
          content="Select Image"
          onChange={handleChange}
        />
        <Image src={imagePreview} rounded centered size="small" />
        <Form.Field
          control={Button}
          disabled={loading}
          size="mini"
          color="green"
          icon="add"
          content="Try Again"
          type="button"
        />
        <Form.Field
          control={Button}
          disabled={disabled || loading}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          type="submit"
        />
      </Form>
      <div style={{ margin: '2em 0' }}>
        <Header as="h2">
          <Icon name="settings" />
          User Permissions
        </Header>
        <Table compact celled definition>
          <Table.Header>
            <Table.Row>
              <Table.HeaderCell />
              <Table.HeaderCell>Name</Table.HeaderCell>
              <Table.HeaderCell>Email</Table.HeaderCell>
              <Table.HeaderCell>Joined</Table.HeaderCell>
              <Table.HeaderCell>Updated</Table.HeaderCell>
              <Table.HeaderCell>Role</Table.HeaderCell>
            </Table.Row>
          </Table.Header>

          <Table.Body>
            {users.map((user) => (
              <UserPermission key={user._id} user={user} />
            ))}
          </Table.Body>
        </Table>
      </div>
    </>
  );
};

export default App;
