import React, { useState } from 'react';
import { Form, Input, Button, Image, Message, Header, Icon } from 'semantic-ui-react';
// import axios from 'axios';
// import baseUrl from './utils/baseUrl';
// import catchErrors from './utils/catchErrors';

function App() {
  const [files, setFiles] = useState([]);
  const [mediaPreview, setMediaPreview] = useState('');
  const [success, setSuccess] = useState(false);
  const [loading, setLoading] = useState(false);
  const [disabled, setDisabled] = useState(true);
  const [error, setError] = useState('');

  return (
    <>
      <Header as="h2" block color="green">
        <Icon name="file" color="green" />
        Upload Receipt or Invoice
      </Header>
      <Form loading={loading} error={Boolean(error)} success={success} onSubmit={''}>
        <Message
          error
          header="Oops! Please check your upload presets on your Klippa account. "
          content={error}
        />
        <Message success icon="check" header="Success!" content="Your file(s) has been uploaded" />
        <Form.Group widths="equal">
          <Form.Field
            control={Input}
            name="media"
            type="file"
            label="Media"
            accept="image/*"
            content="Select Image"
            onChange={''}
          />
        </Form.Group>
        <Image src={mediaPreview} rounded centered size="small" />
        <Form.Field
          control={Button}
          disabled={loading}
          size={''}
          color="green"
          icon="add alternate"
          content="Try Again"
          type="button"
        />
        <Form.Field
          control={Button}
          disabled={disabled || loading}
          color="blue"
          icon="pencil alternate"
          content="Submit"
          float={right}
          type="submit"
        />
      </Form>
    </>
  );
}

export default App;
